Parallel map with do/await<pre><code>package main
import fmt
import arrays

function square(n int) int {
    sleep(20) // simulate work
    return n * n
}

var values = [1, 2, 3, 4, 5]

// Spawn tasks
var tasks = []
for v in values {
    var newTasks, _ = arrays.push(tasks, do square(v))
    tasks = newTasks
}

// Gather results
var results = []
for t in tasks {
    var newResults, _ = arrays.push(results, await(t))
    results = newResults
}

fmt.Println("pmap squares:", results) // [1, 4, 9, 16, 25]
</code></pre>

