Concurrent Stack<pre><code>var shared_stack = collections.new_concurrent_stack()

// Thread-safe operations
function producer() {
    for i in range(100) {
        shared_stack.push(i)
        fmt.Println("Pushed:", i)
    }
}

function consumer() {
    for i in range(50) {
        if !shared_stack.is_empty() {
            var item = shared_stack.pop()
            fmt.Println("Popped:", item)
        }
    }
}

// Start concurrent operations
