    var key = "user_" + user_id.to_string()
    cache.set(key, {"name": "User " + user_id.to_string()}, 60)  // 60 second TTL
}

function get_user(user_id) {
    var key = "user_" + user_id.to_string()
    return cache.get(key)
}

// Concurrent cache operations
