Deadlock Prevention<pre><code>// Always acquire locks in consistent order
var map1 = collections.new_concurrent_map()
var map2 = collections.new_concurrent_map()

function transfer_data(from_map, to_map, key) {
    // Acquire locks in consistent order (by memory address or ID)
    var first_map = if from_map.id() &lt; to_map.id() { from_map } else { to_map }
    var second_map = if from_map.id() &lt; to_map.id() { to_map } else { from_map }

    // This prevents circular wait conditions
    first_map.lock()
    second_map.lock()

    var value = from_map.get(key)
    if value != null {
        to_map.set(key, value)
        from_map.remove(key)
    }

    second_map.unlock()
    first_map.unlock()
}
</code></pre>

