function (lp *LogProcessor) process_batch(batch_size) {
    var batch = []

    // Collect batch
    for i in range(batch_size) {
        if lp.buffer.is_empty() {
            break
        }
        batch.append(lp.buffer.dequeue())
    }

    if batch.size() == 0 {
        return
    }

    // Process batch efficiently
