Expression Evaluation<pre><code>// Parentheses matching
function is_balanced(expression) {
    var stack = collections.new_stack()

    for char in expression {
        if char == "(" || char == "[" || char == "{" {
            stack.push(char)
        } else if char == ")" || char == "]" || char == "}" {
            if stack.is_empty() {
                return false
            }
            var open = stack.pop()
            if !matches(open, char) {
                return false
            }
        }
    }

    return stack.is_empty()
}

function matches(open, close) {
    return (open == "(" &amp;&amp; close == ")") ||
           (open == "[" &amp;&amp; close == "]") ||
           (open == "{" &amp;&amp; close == "}")
}
