        if parseErr == None {
            fmt.Printf("JSON data received: %s\n", data)
        }
    }

    var responseData = {
        "received": {
            "method": method,
            "path": path,
            "queryParams": query,
            "hasBody": body != "",
            "contentType": headers["Content-Type"]
        }
    }

    var jsonResponse, _ = json.Marshal(responseData)
    http.WriteResponse(response, 200, "application/json", jsonResponse)
}
